mapper.select:
select category_id, category_type, category_name, category_status, category_sort, create_time, create_by from biz_category where store_id = #{storeId}
and category_name like concat('%', #{categoryName}, '%')
and category_type = #{categoryType}

mapper.update:
update biz_category
category_name = #{categoryName}, category_type = #{categoryType}, category_status = #{categoryStatus}, category_sort = #{categorySort}, create_time = #{createTime}, create_by = #{createBy}, update_time = #{updateTime}, update_by = #{updateBy},
where category_id = #{categoryId}

mapper.select:
select c.category_id, c.category_name from biz_category c where c.store_id = #{storeId} and c.category_type = #{param.categpryType}
and c.category_name like concat('%',#{param.categoryName},'%')
order by c.category_name
